create table eba_demo_team (
    id        number generated by default on null as identity not null enable,
    action_id number,
    user_id   number,
    role      varchar2(6 char)
);

alter table eba_demo_team
    add constraint eba_demo_team_id_pk primary key ( id )
        using index enable;

alter table eba_demo_team
    add constraint eba_demo_team_role_ck
        check ( role in ( 'MEMBER', 'LEAD' ) ) enable;


