create table eba_vector_datatable (
    id               number generated by default as identity not null enable,
    col1             varchar2(255 byte),
    col2             varchar2(255 byte),
    col3             varchar2(255 byte),
    col4             varchar2(255 byte),
    col5             varchar2(255 byte),
    col6             varchar2(255 byte),
    col7             varchar2(255 byte),
    col8             varchar2(255 byte),
    col9             varchar2(255 byte),
    col10            varchar2(255 byte),
    embedding_vector vector
);

alter table eba_vector_datatable add primary key ( id )
    using index enable;


