create table eba_vector_moviedata (
    id               number generated by default as identity not null enable,
    title            varchar2(255 byte),
    moviedescription varchar2(4000 byte),
    actors           varchar2(1000 byte),
    genre            varchar2(255 byte),
    release_date     date,
    rating           number,
    embedding_vector vector
);

alter table eba_vector_moviedata add primary key ( id )
    using index enable;


